import React, { useState } from "react";
import { connect } from "react-redux";
import { postProduct, getFoodsFromApi } from "../Action/menuActions";
import formatPrix from "../Components/Prix";

const Client = ({ addProduct }) => {
  const [productTitle, setProductTitle] = useState("");
  const [productType, setProductType] = useState("");
  const [productState, setProductState] = useState("");
  const [productPic, setProductPic] = useState("");
  const [productDiscrip, setProductDiscrip] = useState("");
  const [productPrice, setProductPrice] = useState("");

  const componentDidMount= () => {
    this.props.getAllFoods();
  }

  const { foods } = this.props;
  const foodMenu = foods.length ? (
    foods.map((el) => {
      return (
        <div className="carte" key={el._id}>
          <h2>{el.titre}</h2>

          <img className="menu" src={el.photo} alt={el.titre} />

          <p>{el.type}</p>
          <p>{el.etat}</p>
          <p>{formatPrix(el.prix)}</p>

          <button>Envoyer</button>
        </div>
      );
    })
  ) : (
    <p>Le dressings en vitrine n'est pas encore prêt</p>
  );

  return (
    <div>
      <h1>Bienvenue dans votre espace</h1>

      <h2>Mes produits</h2>
      <div>
        <div className="top">{foodMenu}</div>
        <p>
          N.B : Les produits avec un arriere plan jaune sont les produits
          commandés par un client et en cours de reception
        </p>
      </div>
      <div>
        <h2>Ajouter Produit</h2>
        <form>
          <span>Produit</span>
          <input
            type="text"
            placeholder="Entrer le nom de l'article"
            required
            value={productTitle}
            onChange={(e) => setProductTitle(e.target.value)}
          />
          <span>Type</span>
          <input
            type="tel"
            placeholder="Entrer le numero de tel "
            minLength="8"
            maxLength="13"
            required
            value={productType}
            onChange={(e) => setProductType(e.target.value)}
          />
          <span>Etat</span>
          <input
            type="text"
            placeholder="Entrer le mail "
            required
            value={productState}
            onChange={(e) => setProductState(e.target.value)}
          />
          <span>Photo</span>
          <input
            type="text"
            placeholder="Ville, Pays "
            required
            value={productPic}
            onChange={(e) => setProductPic(e.target.value)}
          />
          <span>Description</span>
          <input
            type="number"
            placeholder="Entrer le N° de CIN "
            minLength="8"
            maxLength="8"
            required
            value={productDiscrip}
            onChange={(e) => setProductDiscrip(e.target.value)}
          />
          <span>Prix</span>
          <input
            type="password"
            placeholder="Entrer le mot de passe"
            minLength="8"
            required
            value={productPrice}
            onChange={(e) => setProductPrice(e.target.value)}
          />

          <button
            onClick={() =>
              addProduct({
                titre: productTitle,
                type: productType,
                etat: productState,
                photo: productPic,
                description: productDiscrip,
                prix: productPrice,
              })
            }
          >
            S'inscrire
          </button>
        </form>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({foods: state.foods.foods,});

const mapDispatchToProps = (dispatch) => ({
  addProduct: (foods) => dispatch(postProduct(foods)),
  getAllFoods: () => dispatch(getFoodsFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Client);
