import React, { Component } from "react";
import { connect } from "react-redux";
import { getProductFromApi, selectProduct } from "../Action/productActions";
import Fade from "react-reveal/Fade";
import Zoom from "react-reveal/Zoom";
import Modal from "react-modal";
import formatPrix from "./Prix";

class Product extends Component {
  state = {
    el: null,
    products: [],
    type: "",
    etat: "",
    tri: "",
    filter: [],
  };

  componentDidMount() {
    this.props.getAllProduct();
  }

  // Modal part

  openModal = (el) => {
    this.setState({ el });
  };

  closeModal = () => {
    this.setState({
      el: null,
    });
  };

  // Filter part

  filterType = (e) => {
    console.log(e.target.value);
    if (e.target.value === "" || e.target.value === "TOUT") {
      this.setState({
        type: e.target.value,
        f: this.props.produits,
      });
    } else {
      this.setState({
        type: e.target.value,
        filter: this.props.produits.filter(
          (produit) => produit.type.indexOf(e.target.value) >= 0
        ),
      });
    }
  };

  filterEtat = (e) => {
    console.log(e.target.value);
    if (e.target.value === "" || e.target.value === "TOUT") {
      this.setState({
        etat: e.target.value,
        filter:[],
      });
    } else {
      this.setState({
        etat: e.target.value,
        filter: this.props.produits.filter(
          (produit) => produit.etat.indexOf(e.target.value) >= 0
        ),
      });
    }
  };

  sortPrix = (e) => {
    const tri = e.target.value;
    console.log(e.target.value);
    this.setState((state) => ({
      tri: tri,
      products: this.state.products
        .slice()
        .sort((a, b) =>
          tri === "Moins"
            ? a.prix > b.prix
              ? 1
              : -1
            : tri === "Plus"
            ? a.prix < b.prix
              ? 1
              : -1
            : a._id > b._id
            ? 1
            : -1
        ),
    }));
  };

  render() {
    const { el } = this.state;
    const { produits } = this.props;

    return (
      <div>
        <h1>Articles en Vitrine</h1>
        <div className="filter">
          <div className="filter-type">
            Type :{"  "}
            <select onChange={this.filterType}>
              <option value="">TOUT</option>
              <option value="Decoration">Deco.</option>
              <option value="Service à table">ServiceT.</option>
              <option value="Electromenager">ElectroM.</option>
              <option value="Meuble">Meuble</option>
              <option value="Hytech">Hytech</option>
              <option value="Vetement">Vetement</option>
              <option value="Jouet">Jouet</option>
              <option value="Sport">Sport</option>
              <option value="Outillage">Outillage</option>
            </select>{" "}
          </div>
          <div className="filter-etat">
            Etat :{"  "}
            <select onChange={this.filterEtat}>
              <option value="">TOUT</option>
              <option value="Neuve">Neuve</option>
              <option value="Ancienne">Ancienne</option>
            </select>{" "}
          </div>
          <div className="filter-prix">
            Prix :{"  "}
            <select onChange={this.sortPrix}>
              <option value="">TOUT</option>
              <option value="Moins">Moins cher</option>
              <option value="Plus">Plus cher</option>
            </select>{" "}
          </div>
        </div>
        <Fade bottom cascade={true}>
          <div className="top">
            {produits.length ? (
              this.state.filter.length > 0 ? (
                this.state.filter.map((el) => (
                  <div className="carte" key={el.id}>
                    <h2>{el.titre}</h2>

                    <img
                      className="menu"
                      src={el.photo}
                      alt={el.titre}
                      onClick={() => this.openModal(el)}
                    />

                    <p>{el.type}</p>
                    <p>{el.etat}</p>
                  </div>
                ))
              ) : (
                produits.map((el) => {
                  return (
                    <div className="carte" key={el.id}>
                      <h2>{el.titre}</h2>

                      <img
                        className="menu"
                        src={el.photo}
                        alt={el.titre}
                        onClick={() => this.openModal(el)}
                      />

                      <p>{el.type}</p>
                      <p>{el.etat}</p>
                      <p>{formatPrix(el.prix)}</p>

                      <button
                        title="Click et savour"
                        onClick={() =>
                          this.props.selectProduct({
                            photo: el.photo,
                            type: el.type,
                            etat: el.etat,
                            titre: el.titre,
                            prix: el.prix,
                          })
                        }
                      >
                        Ajouter
                      </button>
                    </div>
                  );
                })
              )
            ) : (
              <p>Les articles en vitrine n'est pas encore prêt</p>
            )}
          </div>
        </Fade>
        {el && (
          <Modal isOpen={true} onRequestClose={this.closeModal}>
            <button className="close-modal" onClick={this.closeModal}>
              x
            </button>
            <Zoom>
              <div className="produit-description">
                <img src={el.photo} alt={el.titre}></img>
                <div className="produit-description-detail">
                  <p className="titre-modal">{el.titre}</p>
                  <p>
                    <strong>Description : </strong> {el.description}
                  </p>
                  <p>
                    <strong>Etat : </strong> {el.etat}
                  </p>
                  <p>
                    <strong>Prix : </strong> {formatPrix(el.prix)}
                  </p>
                </div>
              </div>
            </Zoom>
          </Modal>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    produits: state.produits,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllProduct: () => dispatch(getProductFromApi()),
  selectProduct: (data) => dispatch(selectProduct(data)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Product);
