import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { postProduct, getProductByUserFromApi } from "../Action/productActions";

const Client = ({ postProduct, getProduct, userProds }) => {
  useEffect(() => {
    getProduct();
    console.log("userPPPPP : ", userProds);
  }, []);
  const [productTitle, setProductTitle] = useState("");
  const [productType, setProductType] = useState("");
  const [productState, setProductState] = useState("");
  const [productPic, setProductPic] = useState("");
  const [productDiscrip, setProductDiscrip] = useState("");
  const [productPrice, setProductPrice] = useState("");

  return (
    <div>
      <h1>Bienvenue dans votre espace</h1>
      <div className="Siin">
        <img
          src="https://i.unimedias.fr/2017/02/23/istock-545550406.jpg?auto=format%2Ccompress&crop=faces&cs=tinysrgb&fit=crop&h=227&w=405"
          alt="client space"
        ></img>
      </div>
      <div className="Siiin">
        <h2>Mes produits</h2>
        <div>Liste des Produits</div>
        {userProds ? (
          userProds.map((e) => 
          <div className="carte" key={e._id}>
              {/* <span
                title="Opps! Supprimer!"
                style={{ fontSize: "x-large", cursor: "pointer" }}
                role="img"
                aria-label="Opps! Supprimer!"
                onClick={() => {
                  this.props.delete(e._id);
                }}
              >
                üóëÔ∏è
              </span> */}
              <h2>{e.titre}</h2>
              <img className="menu" src={e.photo} alt="select food" />
              {/* <p>Prix : {formatPrix(e.prix)} </p> */}
            </div>
          )
        ) : (
          <div>Pas d'article mise en vente</div>
        )}
      </div>
      <div className="Siin">
        <h2>Ajouter Produit</h2>
        <form>
          <p>Produit</p>
          <input
            type="text"
            placeholder="Entrer le nom de l'article"
            required
            value={productTitle}
            onChange={(e) => setProductTitle(e.target.value)}
          />
          <p>Type</p>
          <select onChange={(e) => setProductType(e.target.value)}>
            <option value="">--</option>
            <option value="Decoration">Deco.</option>
            <option value="Service √† table">ServiceT.</option>produits
            <option value="Electromenager">ElectroM.</option>
            <option value="Meuble">Meuble</option>
            <option value="Hytech">Hytech</option>
            <option value="Vetement">Vetement</option>
            <option value="Jouet">Jouet</option>
            <option value="Sport">Sport</option>
            <option value="Outillage">Outillage</option>
          </select>
          <p>Etat</p>
          <select onChange={(e) => setProductState(e.target.value)}>
            <option value="Ancienne">Ancienne</option>
            <option value="Neuve">Neuve</option>
          </select>
          <p>Photo</p>
          <input
            type="text"
            placeholder="Donner le lien du photo "
            required
            value={productPic}
            onChange={(e) => setProductPic(e.target.value)}
          />
          <p>Description</p>
          <input
            type="text"
            placeholder="Entrer la description du produit"
            required
            value={productDiscrip}
            onChange={(e) => setProductDiscrip(e.target.value)}
          />
          <p>Prix</p>
          <input
            type="Number"
            placeholder="Entrer le prix"
            required
            value={productPrice}
            onChange={(e) => setProductPrice(e.target.value)}
          />
          <div>
            <button
              onClick={() =>
                postProduct({
                  titre: productTitle,
                  type: productType,
                  etat: productState,
                  photo: productPic,
                  description: productDiscrip,
                  prix: productPrice,
                })
              }
            >
              Ajouter
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    userProds: state.userprod,
  };
};

const mapDispatchToProps = (dispatch) => ({
  postProduct: (product) => dispatch(postProduct(product)),
  getProduct: () => dispatch(getProductByUserFromApi()),
  delete: (_id) => dispatch(deletePanierFromApi(_id)),
  //updatePanier: (el) => dispatch(updatePanierFromAPI(el)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Client);
