import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { GetUsersFromApi } from "../Action/userActions";
import { Icon, Label, Menu, Table } from "semantic-ui-react";

class Dashboard extends Component {
  componentDidMount() {
    this.props.getAllUsers();
  }
  render() {
    return (
      <div>
        (
        <div className="tab">
          <Table celled>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Pseudo</Table.HeaderCell>
                <Table.HeaderCell>Mot de passe</Table.HeaderCell>
                <Table.HeaderCell className="edit">Editer</Table.HeaderCell>
                <Table.HeaderCell>Supprimer</Table.HeaderCell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              {this.props.user.map((el) => (
                <Table.Row>
                  <Table.Cell>
                    <Label ribbon>{el.cin}</Label>
                    {el.name}
                  </Table.Cell>
                  <Table.Cell>{el.t}</Table.Cell>
                  <Table.Cell></Table.Cell>
                  <Table.Cell>
                    <button class="ui button" title="Bloquer">
                      Bloquer
                    </button>
                  </Table.Cell>
                </Table.Row>
              ))}
            </Table.Body>

            <Table.Footer>
              <Table.Row>
                <Table.HeaderCell colSpan="4">
                  <Menu floated="right" pagination>
                    <Menu.Item as="a" icon>
                      <Icon name="chevron left" />
                    </Menu.Item>
                    <Menu.Item as="a">1</Menu.Item>
                    <Menu.Item as="a" icon>
                      <Icon name="chevron right" />
                    </Menu.Item>
                  </Menu>
                </Table.HeaderCell>
              </Table.Row>
            </Table.Footer>
          </Table>
        </div>
        )
        <div>
          <Link to="/enCours">
            <button>Commandes en cours</button>
          </Link>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.users,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllUsers: () => dispatch(GetUsersFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
