import React, { Component } from "react";
import { connect } from "react-redux";
import { getFoodsFromApi, selectFood } from "../Action/menuActions";
import Fade from "react-reveal/Fade";
import Modal from "react-modal";
import Zoom from "react-reveal/Zoom";
import formatPrix from "./Prix";

class Food extends Component {
  componentDidMount() {
    this.props.getAllFoods();
  }
  state = {
    foods: null,
  };
  openModal = (foods) => {
    this.setState({ foods });
  };
  closeModal = () => {
    this.setState({ foods: null });
  };
  render() {
    const { foods } = this.props;
    const foodMenu = foods.length ? (
      foods.map((el) => {
        return (
          <div className="carte" key={el._id}>
            <h2>{el.titre}</h2>
            <a href={"#" + el._id}>
              <img className="menu" src={el.photo} alt={el.titre} />
            </a>
            <p>{el.type}</p>
            <p>{el.etat}</p>
            <p>{el.prix} DNT</p>

            <button
              title="Click et savour"
              onClick={() =>
                this.props.selectFood({
                  photo: el.photo,
                  type: el.type,
                  etat: el.etat,
                  titre: el.titre,
                  prix: el.prix,
                })
              }
            >
              Ajouter
            </button>
          </div>
        );
      })
    ) : (
      <p>Le dressings en vitrine n'est pas encore prÃªt</p>
    );
    return (
      <div>
        <h1>Dressings en Vitrine</h1>
        <Fade bottom cascade={true}>
          <div className="top">{foodMenu}</div>
        </Fade>

      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    foods: state.foods.foods,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllFoods: () => dispatch(getFoodsFromApi()),
  selectFood: (data) => dispatch(selectFood(data)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Food);
