import React, { Component } from "react";
import { connect } from "react-redux";
import { getPanierFromApi } from "../Action/Actions";

class Order extends Component {
  componentDidMount() {
    this.props.getAllPanier();
  }

 /* Change = (e) => {
    let New = e.target.value
    this.setState({prix: this.state.prix * New} )
  } */
  render() {
    const { panier } = this.props;
    const foodPanier = panier.length ? (
      panier.map((el) => {
        return (
          <div className="carte" key={el.id}>
            <h2>{el.name.toUpperCase()}</h2>
            <img className="menu" src={el.photo} />
            <p>Prix : {el.prix} DNT</p>
            <p>
              Quantit√© :{" "}
              <input
                type="number"
                id="tentacles"
                name="tentacles"
                min="1"
                max="100"
                onChange={this.Change}
              />
            </p>
            <p>Total : {el.prix*2}.000 DNT</p>
          </div>
        );
      })
    ) : (
      <h2>
        Votre liste de commande est vide
        <br />
        Veuillez choisir votre menu...
      </h2>
    );
    return (
      <div>
        <h1>Menu du jour</h1>
        <div className="top">{foodPanier}</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    panier: state.panier,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllPanier: () => dispatch(getPanierFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Order);
