import { GET_ALL_PANIER } from "../Action/types";

const initialState = {
  panier: [],
};

export default function panierReducer(state = initialState, action) {
  if (action.type === GET_ALL_PANIER) {
    return {
      panier: action.payload,
    };
  }
  return state;
}
