import { PLUS_UN,  } from "../Action/types";

const initialState = {
  quantité: 4
}

const calcReducer = (state = initialState, action) => {
  if(action == PLUS_UN){
    return {quantité: state.quantité + 1}
  } else if(action == MOINS_UN){
    return {quantité: state.quantité - 1}
  }
  return state;
}

export default calcReducer;
