import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getPanierFromApi,
  deleteFoods,
  totalPrice,
  sumPrice,
  moinsUn,
  plusUn
} from "../Action/Actions";

class Order extends Component {
  componentDidMount() {
    this.props.getAllPanier();
  }

  render() {
