    console.log(this.props.foods.id)
    const { panier } = this.props;
    const foodPanier = panier.length ? (
      panier.map((el) => {
        return (
          <div className="carte" key={el.id}>
            <span
              title="Opps! Supprimer!"
              style={{fontSize:"x-large", cursor: "pointer"}}
              role="img"
              aria-label="Opps! Supprimer!"
