import {
  ADD_USER,
  GET_ALL_PANIER,
  GET_ALL_FOODS,
  SELECT_FOODS,
  PLUS_UN,
  MOINS_UN,
} from "./types";
import Axios from "axios";

/* add  user */
export const addUser = (payload) => ({
  type: ADD_USER,
  payload,
});

/* afficher la nourriture */

export const getAllFoods = (payload) => ({
  type: GET_ALL_FOODS,
  payload: payload,
});

export function getFoodsFromApi() {
  return (dispatch) =>
    Axios.get("http://localhost:3000/foods").then((res) =>
      dispatch(getAllFoods(res.data))
    );
}

/* selectionner la nourriture */

export const selectFood = (payload) => ({
  type: SELECT_FOODS,
  payload,
});

/* ajouter dans panier 


Ajouter = (el) => {
  this.setState((prevState) => ({
    panier: prevState.panier.concat(el),
  }));
};


/* afficher le panier */

export const getAllPanier = (payload) => ({
  type: GET_ALL_PANIER,
  payload,
});

export function getPanierFromApi() {
  return (dispatch) =>
    Axios.get("http://localhost:3000/panier").then((res) =>
      dispatch(getAllPanier(res.data))
    );
}

export function postPanierFromApi() {
  return (dispatch) =>
    Axios.post(`http://localhost:3000/panier`, {"photo": this.state.photo},{"name": this.state.name}, {"compo": this.state.compo},{"compo": this.state.compo}).then((res) =>
      dispatch(getAllPanier(res.data))
    );
}

/* calculer le prix */

export const plusUn = (payload) => ({
  type: PLUS_UN,
  payload,
});

export const moinsUn = (payload) => ({
  type: MOINS_UN,
  payload,
});
