import { PLUS_UN, MOINS_UN } from "../Action/types";

const initialState = {
  quantité: 4
}

const calcReducer = (state = initialState, action) => {
  if(action == PLUS_UN){
    return action.payload +1
  } else if(action == MOINS_UN){
    return action.payload - 1
  }
  return state;
}

export default calcReducer;
