import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { GetUsersFromApi } from "../Action/userActions";
import { Table, Thead, Tbody, Tr, Th, Td } from "react-super-responsive-table";
import "react-super-responsive-table/dist/SuperResponsiveTableStyle.css";

class Dashboard extends Component {
  componentDidMount() {
    this.props.getAllUsers();
  }

  render() {
    const { user } = this.props;
    const toutuser = user.length ? (
    user.map((el) => {
      return (
        <div key={el.id}>
          <h2>{el.name}</h2>
          <p>{el.tel}</p>
        </div>
      );
    })):("non")
    return (
      <div>
        <h1>TOUT LES CLIENTS</h1>

       <div className="T-T">
          <Table className="table">
            <Thead>
              <Tr>
                <Th className="table">Client</Th>
                <Th className="table">C.I.N</Th>
                <Th className="table">Mail</Th>
                <Th className="table">Tel.</Th>
              </Tr>
            </Thead>
{/*  
            <Tbody>
              {this.props.user.map((el) => (
                <Tr>
                  <Td className="table"> {el.name} </Td>
                  <Td className="table"> {el.cin} </Td>
                  <Td className="table"> {el.mail} </Td>
                  <Td className="table"> {el.tel} </Td>
                </Tr>
              ))}
            </Tbody>
             */}
          </Table>

        </div> 
        <div>
        toutuser
        </div>
        <div>
          <Link to="/enCours">
            <button>Commandes en cours</button>
          </Link>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    user: state.user,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllUsers: () => dispatch(GetUsersFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
