import React, { Component } from "react";
import { connect } from "react-redux";
import { getPanierFromApi, deleteFoods } from "../Action/Actions";

class Order extends Component {
  componentDidMount() {
    this.props.getAllPanier();
  }

  render() {
    const { panier } = this.props;
    const foodPanier = panier.length ? (
      panier.map((el) => {
        return (
          <div className="carte" key={el.id}>
            <button
              title="Click et savour"
              onClick={() => this.props.delete(el)}
            >
              X
            </button>
            <h2>{el.name}</h2>

            <img className="menu" src={el.photo} alt="select food" />
            <p>Prix : {el.prix} DNT </p>
            <div>
              Quantité :{" "}
              <input
                type="number"
                name="quantity"
                min="1"
                max="100"
                style={{ fontFamily: "fantasy", fontSize: "meduim" }}
              />
            </div>
            <p>Total : {this.props.total} DNT</p>
          </div>
        );
      })
    ) : (
      <h2>
        Votre liste de commande est vide
        <br />
        Veuillez choisir votre menu...
      </h2>
    );
    return (
      <div>
        <h1>Vous avez Commandé ...</h1>
        <div className="top">{foodPanier}</div>
        <h2>Prix Total de la Commande : <span onChange={() => this.props.sum()}></span> TND</h2>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    panier: state.foods.panier,
    quantité: state.quantité,

  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllPanier: () => dispatch(getPanierFromApi()),
  delete: () => dispatch(deleteFoods()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Order);
