import React, { Component } from "react";
import { connect } from "react-redux";
import { getProductFromApi, selectProduct } from "../Action/productActions";
import Fade from "react-reveal/Fade";
import formatPrix from "./Prix";

class Product extends Component {
  componentDidMount() {
    this.props.getAllProduct();
  }

  filterType = (e) => {
    console.log(e.target.value);
    if (e.target.value === "" || e.target.value === "TOUT") {
      this.setState({
        type: e.target.value,
        products: data.products,
      });
    } else {
      this.setState({
        type: e.target.value,
        products: data.products.filter(
          (produit) => produit.type.indexOf(e.target.value) >= 0
        ),
      });
    }
  };

  filterEtat = (e) => {
    console.log(e.target.value);
    if (e.target.value === "" || e.target.value === "TOUT") {
      this.setState({
        etat: e.target.value,
        products: data.products,
      });
    } else {
      this.setState({
        etat: e.target.value,
        products: data.products.filter(
          (produit) => produit.etat.indexOf(e.target.value) >= 0
        ),
      });
    }
  };

  sortPrix = (e) => {
    const tri = e.target.value;
    console.log(e.target.value);
    this.setState((state) => ({
      tri: tri,
      products: this.state.products
        .slice()
        .sort((a, b) =>
          tri === "Moins"
            ? a.prix > b.prix
              ? 1
              : -1
            : tri === "Plus"
            ? a.prix < b.prix
              ? 1
              : -1
            : a._id > b._id
            ? 1
            : -1
        ),
    }));
  };

  render() {
    const { produits } = this.props;
    const toutProduct = produits.length ? (
      produits.map((el) => {
        return (
          <div className="carte" key={el._id}>
            <h2>{el.titre}</h2>

            <img className="menu" src={el.photo} alt={el.titre} />

            <p>{el.type}</p>
            <p>{el.etat}</p>
            <p>{formatPrix(el.prix)}</p>

            <button
              title="Click et savour"
              onClick={() =>
                this.props.selectProduct({
                  photo: el.photo,
                  type: el.type,
                  etat: el.etat,
                  titre: el.titre,
                  prix: el.prix,
                })
              }
            >
              Ajouter
            </button>
          </div>
        );
      })
    ) : (
      <p>Le dressings en vitrine n'est pas encore prêt</p>
    );
    return (
      <div>
        <h1>Dressings en Vitrine</h1>

        <Fade bottom cascade={true}>
          <div className="top">{toutProduct}</div>
        </Fade>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    produits: state.produits,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllProduct: () => dispatch(getProductFromApi()),
  selectProduct: (data) => dispatch(selectProduct(data)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Product);
