import React, { Component } from "react";
import { connect } from "react-redux";
import { GetUsersFromApi } from "../Action/userActions";
import Fade from "react-reveal/Fade";
import Zoom from "react-reveal/Zoom";
import Modal from "react-modal";
import formatPrix from "./Prix";
import Filter from "./Filter";

class Dashboard extends Component {
  state = {
    el: null,
  };

  componentDidMount() {
    this.props.getAllUser();
  }

  openModal = (el) => {
    this.setState({ el });
  };

  closeModal = () => {
    this.setState({
      el: null,
    });
  };

  render() {
    const { el } = this.state;
    const { user } = this.props;
    const toutUsers = user.length ? (
      user.map((el) => {
        return (
          <div className="carte" key={el.id}>
            <h2 
              onClick={() => this.openModal(el)}>{el.titre}</h2>





          </div>
        );
      })
    ) : (
      <p>La liste des clients n'est pas encore prÃªte</p>
    );
    return (
      <div>
        <h1>Articles en Vitrine</h1>
        <Filter />
        <Fade bottom cascade={true}>
          <div className="top">{toutUsers}</div>
        </Fade>
        {el && (
          <Modal isOpen={true} onRequestClose={this.closeModal}>
            <button className="close-modal" onClick={this.closeModal}>
              x
            </button>
            <Zoom>
              <div className="produit-description">
                <div className="produit-description-detail">
                  <p className="titre-modal">{el.name}</p>
                  <p>
                    <strong>Description : </strong> {el.description}
                  </p>
                  <p>
                    <strong>Etat : </strong> {el.etat}
                  </p>
                  <p>
                    <strong>Prix : </strong> {formatPrix(el.prix)}
                  </p>
                </div>
              </div>
            </Zoom>
          </Modal>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    produits: state.produits,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllUser: () => dispatch(GetUsersFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
