import {
  ADD_USER,
  GET_ALL_PANIER,
  GET_ALL_FOODS,
  SELECT_FOODS,
  PLUS_UN,
  MOINS_UN,
} from "./types";
import Axios from "axios";

/* add  user */
export const addUser = (payload) => ({
  type: ADD_USER,
  payload,
});

/* afficher la nourriture */

export const getAllFoods = (payload) => ({
  type: GET_ALL_FOODS,
  payload: payload,
});

export function getFoodsFromApi() {
  return (dispatch) =>
    Axios.get("http://localhost:3000/foods").then((res) =>
      dispatch(getAllFoods(res.data))
    );
}

/* ajouter au panier */

export function selectFood(el) {
  return (dispatch) =>
    Axios.post(`http://localhost:3000/panier`, {
      photo: el.photo,
      name: el.name,
      compo: el.compo,
      prix: el.prix,
    })
      .then((res) => dispatch(addToPanier(el), alert(res)))
      .catch((err) => console.log(err));
}

export const addToPanier = (payload) => ({
  type: SELECT_FOODS,
  payload,
});

/* afficher le panier */

export const getAllPanier = (payload) => ({
  type: GET_ALL_PANIER,
  payload,
});

export function getPanierFromApi() {
  return (dispatch) =>
    Axios.get("http://localhost:3000/panier").then((res) =>
      dispatch(getAllPanier(res.data))
    );
}

/*delette user */

export const deleteUser = (payload) => ({
  type: DELETE_USERS,
  payload,
});

/* calculer le prix */

export const plusUn = (payload) => ({
  type: PLUS_UN,
  payload,
});

export const moinsUn = (payload) => ({
  type: MOINS_UN,
  payload,
});
