import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { GetUsersFromApi } from "../Action/Actions";

class SignUp extends Component {
  verif() {
    const x = this.props.user.filter((el) => {
      console.log('eeee');
    });

    console.log(x);
  }
  componentDidMount() {
    this.props.getAlluser();
    console.log("eeddd", this.props.user);
  }
  render() {
    return (
      <div>
        <div className="Sin">
          <div className="Siin">
            <h1>s'Authentifier</h1>
            <Link to="/cxn">
              <button className="btn"> Inscription</button>
            </Link>{" "}
            <form>
              <span>Nom d'utilisateur</span>
              <input
                type="text"
                placeholder="Entrer le nom d'utilisateur"
                required
                onChange={(e) => this.setState({ name: e.target.value })}
              />
              <span>Mot de passe</span>
              <input
                type="password"
                placeholder="Entrer le mot de passe"
                onChange={(e) => this.setState({ pass: e.target.value })}
              />
              <button onClick={() => this.verif()}>Se connecter</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.user,
});
const mapDispatchToProps = (dispatch) => ({
  getAlluser: () => dispatch(GetUsersFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
