import React, { Component } from 'react'

export class Order extends Component {
  render() {
    const { panier } = this.props;
    const foodPanier = foods.length ? (
      foods.map((el) => {
        return (
          <div className="carte" key={el.id}>

            <img className="menu" src={el.photo} />
            <span>{el.name.toUpperCase()}</span>
            <span>{el.prix} DNT</span>

            <button title="Click et savour">Commandez</button>
          </div>
        );
      })
    ) : (
      <h2>Votre liste de commande est vide <br/>
        Veuillez choisir votre menu...</h2>
    );
    return (
      <div>

      </div>
    )
  }
}

export default Order
