import { ADD_USER, GET_ALL_FOODS, SELECT_FOODS } from './types';
import Axios from 'axios';



/* get toute la nourriture */

export const getAllFoods = (payload) => ({
  type: GET_ALL_FOODS,
  payload,
});

export function getFoodsFromApi() {
  return (dispatch) =>
    Axios.get('http://localhost:3000/foods').then((res) =>
      dispatch(getAllFoods(res.data))
    );
}

/* ajouter au panier */

export const ajouterAuPanier = (payload) => ({
  type: SELECT_FOODS,
  payload,
});
