import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { GetUsersFromApi } from "../Action/userActions";
//import { Table, Thead, Tbody, Tr, Th, Td } from "react-super-responsive-table";
//import "react-super-responsive-table/dist/SuperResponsiveTableStyle.css";
import { Icon, Label, Menu, Table, Dropdown } from "semantic-ui-react";

class Dashboard extends Component {
  componentDidMount() {
    this.props.getAllUsers();
  }

  render() {
    return (
      <div>
        <h1>TOUT LES CLIENTS</h1>

        <Table celled>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Pseudo</Table.HeaderCell>
              <Table.HeaderCell>Mot de passe</Table.HeaderCell>
              <Table.HeaderCell className="edit">Editer</Table.HeaderCell>
              <Table.HeaderCell>Supprimer</Table.HeaderCell>
            </Table.Row>
          </Table.Header>
          <Table.Body>
            {this.props.users.map((el) => (
              <Table.Row>
                <Table.Cell>
                  <Label ribbon>{el.userPost}</Label>
                  {el.userName}
                </Table.Cell>
                <Table.Cell>{el.passWord}</Table.Cell>
                <Table.Cell>
                  <ModalEdite el={el} />{" "}
                </Table.Cell>
                <Table.Cell>
                  <button
                    class="ui button"
                    title="Supprimer"
                    onClick={() => {
                      this.props.delete(el.id);
                    }}
                  >
                    Supprimer
                  </button>
                </Table.Cell>
              </Table.Row>
            ))}
          </Table.Body>

          <Table.Footer>
            <Table.Row>
              <Table.HeaderCell colSpan="4">
                <Menu floated="right" pagination>
                  <Menu.Item as="a" icon>
                    <Icon name="chevron left" />
                  </Menu.Item>
                  <Menu.Item as="a">1</Menu.Item>
                  <Menu.Item as="a" icon>
                    <Icon name="chevron right" />
                  </Menu.Item>
                </Menu>
              </Table.HeaderCell>
            </Table.Row>
          </Table.Footer>
        </Table>


        {/* <div className="T-T">
          <Table className="table">
            <Thead>
              <Tr>
                <Th className="table">Client</Th>
                <Th className="table">C.I.N</Th>
                <Th className="table">Mail</Th>
                <Th className="table">Tel.</Th>
              </Tr>
            </Thead>

            <Tbody>
              {this.props.user.map((el) => (
                <Tr>
                  <Td className="table"> {el.name} </Td>
                  <Td className="table"> {el.cin} </Td>
                  <Td className="table"> {el.mail} </Td>
                  <Td className="table"> {el.tel} </Td>
                </Tr>
              ))}
            </Tbody>
          </Table>
        </div> */}
        <div>
          <Link to="/enCours">
            <button>Commandes en cours</button>
          </Link>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    user: state.user,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllUsers: () => dispatch(GetUsersFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
