import React, { Component } from "react";
import "./App.css";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Nav from "./Components/Nav/Nav";
import Carte from "./Container/Carte";
import Commande from "./Container/Commande";
import Connexion from "./Container/Connexion";
import Deconnexion from "./Container/Decnx";
import Auto from "./Container/Auten";
import Accueil from "./Container/Accueil";
import Orders from "./Container/Orders";
import Clients from "./Container/Clients";
import Admin from "./Container/Admin";
// import { connect } from "react-redux";
// import { get_current_user } from './actions/currentUserAction'

//json-server --watch db.json

export default class App extends Component {
  //componentDidMount() { this.props.get_current_user() }

  render() {
    return (
      <div>
        <Router>
          <Nav />
          {this.props.user ? (
            this.props.user === "none" ? (
              <Switch>
                <Route exact path="/" component={Accueil} />
                <Route exact path="/cnx" component={Connexion} />
                <Route path="/auto">
                  <Auto />
                </Route>
                <Route path="/cxn">
                  <Connexion />
                </Route>
              </Switch>
            ) : this.props.user.role === "Administrateur" ? (
              <Switch>
                <Route exact path="/admin" component={Admin} />

                <Route exact path="/" component={Accueil} />
                <Route exact path="/cnx" component={Connexion} />

                <Route exact path="/home" component={Carte} />
                <Route exact path="/signup" component={Deconnexion} />
                <Route path="/auto">
                  <Auto />
                </Route>
                <Route path="/cxn">
                  <Connexion />
                </Route>
                <Route path="/carte">
                  <Carte />
                </Route>
                <Route path="/enCours">
                  <Orders />
                </Route>
              </Switch>
            ) : this.props.user.role === "Client" ? (
              <Switch>
                <Route exact path="/client" component={Clients} />
                <Route exact path="/" component={Accueil} />
                <Route exact path="/cnx" component={Connexion} />
                <Route exact path="/home" component={Carte} />
                <Route exact path="/signup" component={Deconnexion} />
                <Route exact path="/panier" component={Commande} />
                <Route path="/auto">
                  <Auto />
                </Route>
                <Route path="/cxn">
                  <Connexion />
                </Route>
                <Route path="/carte">
                  <Carte />
                </Route>
                <Route path="/enCours">
                  <Orders />
                </Route>
              </Switch>
            ) : (
              <div>euhhh1 !{console.log("user", this.props.user)}</div>
            )
          ) : (
            <div>euhhh2 !</div>
          )}
        </Router>
      </div>
    );
  }
}

