import React, { useState } from "react";
import { connect } from "react-redux";
import { postUser } from "../Action/Actions";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Auten from "./Auten";

const SingIn = ({ addPerson }) => {
  const [userName, setUserName] = useState("");
  const [userMail, setUserMail] = useState("");
  const [userPass, setUserPass] = useState("");
  return (
    <div className="Sin">
      <div className="Siin">
        <h1>Connexion</h1>

        <Router>
          <Link to="/auth">
            {" "}
            <button className="btn">Inscription</button>
            <button className="btn"> Authentification</button>
          </Link>
          <Route exact path="/auth" component={Auten} />
        </Router>
        <form>
          <span>Nom d'utilisateur</span>
          <input
            type="text"
            placeholder="Entrer le nom d'utilisateur"
            required
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
          <span>Mail</span>
          <input
            type="text"
            placeholder="Entrer le mail "
            required
            value={userMail}
            onChange={(e) => setUserMail(e.target.value)}
          />
          <span>Mot de passe</span>
          <input
            type="password"
            placeholder="Entrer le mot de passe"
            minLength="8"
            required
            value={userPass}
            onChange={(e) => setUserPass(e.target.value)}
          />
          <button
            onClick={() =>
              addPerson({
                name: userName,
                mail: userMail,
                pass: userPass,
              })
            }
          >
            Envoyer
          </button>
        </form>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({});

const mapDispatchToProps = (dispatch) => ({
  addPerson: (user) => dispatch(postUser(user)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SingIn);
