import React, { Component } from "react";
import { connect } from "react-redux";
import { connectUserFromApi ,logoutUser, getCurrentUser} from "../../../Action/userActions";
import { NavLink } from "react-router-dom";

class Link extends Component {
  componentDidMount = () => {
    this.props.connectUser();
    this.props.getCurrentUser();
  };
  render() {
    const { user } = this.props;
    console.log("idexx", user)
    // const auto = this.props.user && this.props.user.role;
    // console.log(
    //   "index",
    //   this.props.user
    // );
    // console.log("role", this.props.user.name)
    // console.log(auto);
    return (
      <div>
        <div className="link">
          <NavLink to="/admin">Mon Espace</NavLink>
          <NavLink to="/client">Mon Espace</NavLink>
          <NavLink to="/home">Produits</NavLink>
          <NavLink to="/auto">Connexion</NavLink>
          <NavLink
            to="/signup"
            onClick={() => {
              this.props.logoutUser();
            }}
          >
            Deconnexion
          </NavLink>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.role,
});
const mapDispatchToProps = (dispatch) => ({
  connectUser: () => dispatch(connectUserFromApi()),
  logoutUser: () => dispatch(logoutUser()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Link);
