import React, { Component } from "react";
import { connect } from "react-redux";
import { getFoodsFromApi, selectFood } from "../Action/menuActions";
import Fade from "react-reveal/Fade";
import Modal from "react-modal";
import Zoom from "react-reveal/Zoom";

class Food extends Component {
  componentDidMount() {
    this.props.getAllFoods();
  }
  state = {
    foods: null,
  };
  openModal = (product) => {
    this.setState({ product });
  };
  closeModal = () => {
    this.setState({ product: null });
  };
  render() {
    const { foods } = this.props;
    const foodMenu = foods.length ? (
      foods.map((el) => {
        return (
          <div className="carte" key={el.id}>
            <h2>{el.titre}</h2>
            <img className="menu" src={el.photo} alt="Mon Menu" />
            <p>{el.type}</p>
            <p>{el.etat}</p>
            <p>{el.prix} DNT</p>

            <button
              title="Click et savour"
              onClick={() =>
                this.props.selectFood({
                  photo: el.photo,
                  type: el.type,
                  etat: el.etat,
                  titre: el.titre,
                  prix: el.prix,
                })
              }
            >
              Ajouter
            </button>
          </div>
        );
      })
    ) : (
      <p>Le dressings en vitrine n'est pas encore prêt</p>
    );
    return (
      <div>
        <h1>Dressings en Vitrine</h1>
        <Fade bottom cascade={true}>
          <div className="top">{foodMenu}</div>
        </Fade>
        {product && (
          <Modal isOpen={true} onRequestClose={this.closeModal}>
            <Zoom>
              <button className="close-modal" onClick={this.closeModal}>
                x
              </button>
              <div className="produit-description">
                <img src={product.photo} alt={product.titre}></img>
                <div className="produit-description-detail">
                  <p className="titre-modal">{product.titre}
                  </p>
                  <p>
                    <strong>Description : </strong> {product.description}
                  </p>
                  <p>
                    <strong>Etat : </strong> {product.etat}
                  </p>
                  <p>
                    <strong>Prix : </strong> {formatPrix(product.prix)}
                  </p>
                </div>
              </div>
            </Zoom>
          </Modal>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    foods: state.foods.foods,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllFoods: () => dispatch(getFoodsFromApi()),
  selectFood: (data) => dispatch(selectFood(data)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Food);
