import { GET_ALL_PRODUCT, ADD_PRODUCT, FILTER_ETAT } from "../Action/types";

const initialState = {
  produits: [],
};

export default function productReducer(state = initialState, action) {
  if(action.payload === FILTER_ETAT){
    return{
      ...state,
      fetat: action.payload.fetat,
      filterItem: action.payload.ite
    }
  }
  if (action.type === GET_ALL_PRODUCT) {
    return action.payload;
  }
  if (action.type === ADD_PRODUCT)
    return { ...state, products: { ...action.payload, ...state.products } };
  return state;
}
