import React, { Component } from "react";
import { connect } from "react-redux";
import { getPanierFromApi, plusUn, moinsUn } from "../Action/Actions";

class Order extends Component {
  componentDidMount() {
    this.props.getAllPanier();
  }

  render() {
    const { panier } = this.props;
    const foodPanier = panier.length ? (
      panier.map((el) => {
        return (
          <div className="carte" key={el.id}>
            <h3>X</h3>
            <h2>{el.name.toUpperCase()}</h2>

            <img className="menu" src={el.photo} />
            <p>Prix : {el.prix} DNT </p>
            <div>Quantité : <button onClick={this.props.moinsUn()}>◄</button>{this.props.quantité.quantité} <span onClick={this.props.plusUn()}>►</span>  </div>
            <p>Total : {el.prix * this.props.quantité.quantité} DNT</p>
          </div>
        );
      })
    ) : (
      <h2>
        Votre liste de commande est vide
        <br />
        Veuillez choisir votre menu...
      </h2>
    );
    return (
      <div>
        <h1>Vous avez Commandé ...</h1>
        <div className="top">{foodPanier}</div>
        <h2>Prix Total de la Commande : ... TND</h2>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    panier: state.panier,
    quantité: state.quantité,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllPanier: () => dispatch(getPanierFromApi()),
  plusUn: () => dispatch(plusUn()),
  moinsUn: () => dispatch(moinsUn())
});

export default connect(mapStateToProps, mapDispatchToProps)(Order);
