import React, { Component } from "react";
import { connect } from "react-redux";
import { filterEtat, filterType, sortPrix } from "../Action/productActions";

class Filter extends Component {
  render() {
    return !this.props.filteredProducts ? (
      <div>Loading...</div>
    ) : (
      <div className="filter">
        <div className="filter-type">
          Type :{"  "}
          <select value={this.props.type} onChange={(e)=>this.props.filterType(this.props.produits, e.target.value)}>
            <option>TOUT</option>
            <option value="Decoration">Deco.</option>
            <option value="Service à table">ServiceT.</option>
            <option value="Electromenager">ElectroM.</option>
            <option value="Meuble">Meuble</option>
            <option value="Hytech">Hytech</option>
            <option value="Vetement">Vetement</option>
            <option value="Jouet">Jouet</option>
            <option value="Sport">Sport</option>
            <option value="Outillage">Outillage</option>
          </select>{" "}
        </div>
        <div className="filter-etat">
          Etat :{"  "}
          <select value={this.props.etat} onChange={(e)=>this.props.filterE(this.props.produits, e.target.value)}>
            <option>TOUT</option>
            <option value="Neuve">Neuve</option>
            <option value="Ancienne">Ancienne</option>
          </select>{" "}
        </div>
        <div className="filter-prix">
          Prix :{"  "}
          <select value={this.props.tri} onChange={(e) => this.props.sortPrix(this.props.filteredProducts, e.target.value)}>
            <option>TOUT</option>
            <option value="Moins">Moins cher</option>
            <option value="Plus">Plus cher</option>
          </select>{" "}
        </div>
      </div>
    );
  }
}

export default connect(
  (state) => ({
    fetat: state.produits.fetat,
    ftype: state.produits.ftype,
    sort: state.produits.sort,
    produits: state.produits.items,
    filteredProducts: state.produits.filteredItems,
  }),
  { filterType, filterEtat, sortPrix }
)(Filter);
