import React, { Component } from 'react';
import { connect } from "react-redux";
import { getFoodsFromApi } from "../Action/Actions";

export class Order extends Component {
  componentDidMount() {
    this.props.getAllFoods();
  }
  render() {
    const { panier } = this.props;
    const foodPanier = panier.length ? (
      panier.map((el) => {
        return (
          <div className="carte" key={el.id}>

            <img className="menu" src={el.photo} />
            <span>{el.name.toUpperCase()}</span>
            <span>{el.prix} DNT</span>
            <input type="number" maxLength="10" />
            <span>{el.prix} DNT</span>
            <button title="Click et savour">Commandez</button>
          </div>
        );
      })
    ) : (
      <h2>Votre liste de commande est vide <br/>
        Veuillez choisir votre menu...</h2>
    );
    return (
      <div>

      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    foods: state.foods,
  };
};

const mapDispatchToProps = (dispatch) => ({
  getAllFoods: () => dispatch(getFoodsFromApi()),
});


export default connect(mapStateToProps, mapDispatchToProps)(Order);
